let cartas = [cartaFloripa, cartaSp, cartaLondres, cartaToronto, cartaSantos, cartaNovaIorque];
let cartaMaquina;
let cartaJogador;

function sortearCarta() {
	let indiceCartaMaquina = parseInt(Math.random() * 6);
	cartaMaquina = cartas[indiceCartaMaquina];

	let indiceCartaJogador = parseInt(Math.random() * 6);
	while (indiceCartaJogador === indiceCartaMaquina) {
		indiceCartaJogador = parseInt(Math.random() * 6);
	}
	cartaJogador = cartas[indiceCartaJogador];

	document.getElementById('btnSortear').disabled = true;
	document.getElementById('btnJogar').disabled = false;

	exibirCartaJogador();
}

function exibirCartaJogador() {
	let divCartaJogador = document.getElementById('carta-jogador');
	let moldura = '<img src="https://www.alura.com.br/assets/img/imersoes/dev-2021/card-super-trunfo-transparent.png" style=" width: inherit; height: inherit; position: absolute;">';
	divCartaJogador.style.backgroundImage = `url(${cartaJogador.imagem})`;
	let nome = `<p class="carta-subtitle">${cartaJogador.nome}</p>`;
	let opcoesTexto = "";

	for (let atributo in cartaJogador.atributos) {
		opcoesTexto += "<input type='radio' name='atributo' value='" + atributo + "'>" + atributo + " " + cartaJogador.atributos[atributo] + "<br>";
	}

	let html = "<div id='opcoes' class='carta-status'>";

	divCartaJogador.innerHTML = moldura + nome + html + opcoesTexto + '</div>';
}

function obterAtributo () {
	let radioAtributo = document.getElementsByName('atributo');
	for (let i = 0; i < radioAtributo.length; i++) {
		if (radioAtributo[i].checked) {
			return radioAtributo[i].value;
		}
	}
}

function jogar() {
	let divResultado = document.getElementById("resultado");
	let atributoSelecionado = obterAtributo();

	if (cartaJogador.atributos[atributoSelecionado] > cartaMaquina.atributos[atributoSelecionado]) {
		htmlResultado = `<p class="resultado-final">Venceu</p>`;
	} else if(cartaJogador.atributos[atributoSelecionado] < cartaMaquina.atributos[atributoSelecionado]) {
		htmlResultado = `<p class="resultado-final">Perdeu</p>`;
	} else {
		htmlResultado = `<p class="resultado-final">Empatou</p>`;
	}

	divResultado.innerHTML = htmlResultado;
	exibeCartaMaquina();
}

function exibeCartaMaquina() {
	let divCartaMaquina = document.getElementById('carta-maquina');
	let moldura = '<img src="https://www.alura.com.br/assets/img/imersoes/dev-2021/card-super-trunfo-transparent.png" style=" width: inherit; height: inherit; position: absolute;">';
	divCartaMaquina.style.backgroundImage = `url(${cartaMaquina.imagem})`;
	let nome = `<p class="carta-subtitle">${cartaMaquina.nome}</p>`;

	var opcoesTexto = ""

    for (var atributo in cartaMaquina.atributos) {
        console.log(atributo)
        opcoesTexto += "<p type='text' name='atributo' value='" + atributo + "'>" + atributo + " " + cartaMaquina.atributos[atributo] + "<br>"
    }

    let html = "<div id='opcoes' class='carta-status --spacing'>"

    divCartaMaquina.innerHTML = moldura + nome + html + opcoesTexto + '</div>'
}

